/* Read one character from the current input source of 'yy_lexer'. */
static int yy_read_character(input_scan_lexer_t *yy_lexer)
{
  int c;

  *(yy_lexer->yy_c_buf_p) = yy_lexer->yy_hold_char;

  if (*(yy_lexer->yy_c_buf_p) == YY_END_OF_BUFFER_CHAR) {
    /* yy_c_buf_p now points to the character we want to return.
     * If this occurs *before* the EOB characters, then it's a
     * valid NUL; if not, then we've hit the end of the buffer.
     */
    if (yy_lexer->yy_c_buf_p <
          &yy_lexer->yy_current_buffer->yy_ch_buf[yy_lexer->yy_n_chars]) {
      /* This was really a NUL. */
      *(yy_lexer->yy_c_buf_p) = '\0';
    }

    else { /* need more input */
      int offset = yy_lexer->yy_c_buf_p - yy_lexer->yy_text_ptr;
      ++(yy_lexer->yy_c_buf_p);

      switch (yy_get_next_buffer(yy_lexer)) {
        case EOB_ACT_LAST_MATCH:
          /* This happens because yy_g_n_b()
           * sees that we've accumulated a
           * token and flags that we need to
           * try matching the token before
           * proceeding.  But for input(),
           * there's no matching to consider.
           * So convert the EOB_ACT_LAST_MATCH
           * to EOB_ACT_END_OF_FILE.
           */

          /* Reset buffer status. */
          input_scan_restart(yy_lexer, yy_lexer->yy_input_stream);

          /* fall through */

        case EOB_ACT_END_OF_FILE: {
          if (input_scan_wrap(yy_lexer))
            return EOF;

          if (!yy_lexer->yy_did_buffer_switch_on_eof)
            YY_NEW_FILE;

          return yy_read_character(yy_lexer);
        }

        case EOB_ACT_CONTINUE_SCAN:
          yy_lexer->yy_c_buf_p = yy_lexer->yy_text_ptr + offset;
          break;
      }
    }
  }

  c = *(unsigned char *)yy_lexer->yy_c_buf_p;   /* cast for 8-bit char's */
  *(yy_lexer->yy_c_buf_p) = '\0';     /* preserve yy_text */
  yy_lexer->yy_hold_char = *++(yy_lexer->yy_c_buf_p);

  yy_lexer->yy_current_buffer->yy_at_bol = (c == '\n');

  return c;
}


void input_scan_restart(input_scan_lexer_t *yy_lexer, FILE *input_file)
{
  if (!yy_lexer->yy_current_buffer) {
    yy_lexer->yy_current_buffer =
      input_scan_create_buffer(yy_lexer, yy_lexer->yy_input_stream, YY_BUF_SIZE);
  }

  input_scan_init_buffer(yy_lexer, yy_lexer->yy_current_buffer, input_file);
  input_scan_load_buffer_state(yy_lexer);
}



void input_scan_switch_to_buffer(input_scan_lexer_t *yy_lexer, INPUT_SCAN_BUFFER_STATE new_buffer)
{
  if (yy_lexer->yy_current_buffer == new_buffer) {
    return;
  }

  if (yy_lexer->yy_current_buffer) {
    /* Flush out information for old buffer. */
    *(yy_lexer->yy_c_buf_p) = yy_lexer->yy_hold_char;
    yy_lexer->yy_current_buffer->yy_buf_pos = yy_lexer->yy_c_buf_p;
    yy_lexer->yy_current_buffer->yy_n_chars = yy_lexer->yy_n_chars;
  }

  yy_lexer->yy_current_buffer = new_buffer;
  input_scan_load_buffer_state(yy_lexer);

  /* We don't actually know whether we did this switch during
   * EOF (input_scan_wrap()) processing, but the only time this flag
   * is looked at is after input_scan_wrap() is called, so it's safe
   * to go ahead and always set it.
   */
  yy_lexer->yy_did_buffer_switch_on_eof = 1;
}



static void input_scan_load_buffer_state(input_scan_lexer_t *yy_lexer)
{
  yy_lexer->yy_n_chars = yy_lexer->yy_current_buffer->yy_n_chars;
  yy_lexer->yy_text_ptr = yy_lexer->yy_c_buf_p = yy_lexer->yy_current_buffer->yy_buf_pos;
  yy_lexer->yy_input_stream = yy_lexer->yy_current_buffer->yy_input_file;
  yy_lexer->yy_hold_char = *(yy_lexer->yy_c_buf_p);
}


INPUT_SCAN_BUFFER_STATE input_scan_create_buffer(input_scan_lexer_t *yy_lexer,
                                 FILE *file, int size)
{
  INPUT_SCAN_BUFFER_STATE b;

  b = (INPUT_SCAN_BUFFER_STATE)yy_flex_alloc(sizeof(input_scan_buffer_state));
  if (!b) {
    YY_FATAL_ERROR("out of dynamic memory in input_scan_create_buffer()");
  }

  b->yy_buf_size = size;

  /* yy_ch_buf has to be 2 characters longer than the size given because
   * we need to put in 2 end-of-buffer characters.
   */
  b->yy_ch_buf = (char*)yy_flex_alloc(b->yy_buf_size + 2);
  if (!b->yy_ch_buf) {
    YY_FATAL_ERROR("out of dynamic memory in input_scan_create_buffer()");
  }

  b->yy_is_our_buffer = 1;

  input_scan_init_buffer(yy_lexer, b, file);

  return b;
}



void input_scan_delete_buffer(input_scan_lexer_t *yy_lexer, INPUT_SCAN_BUFFER_STATE b)
{
  if (!b) {
    return;
  }

  if (yy_lexer->yy_current_buffer == b) {
    /* Safety measure. */
    yy_lexer->yy_current_buffer = NULL;
  }

  if (b->yy_is_our_buffer) {
    yy_flex_free((void*)b->yy_ch_buf);
  }

  yy_flex_free((void*)b);
}



static void input_scan_init_buffer(input_scan_lexer_t *yy_lexer,
                           INPUT_SCAN_BUFFER_STATE b, FILE *file)
{
  input_scan_flush_buffer(yy_lexer, b);

  b->yy_input_file = file;
  b->yy_fill_buffer = 1;

  b->yy_is_interactive = 0;
  YY_DEBUG_LOG_CALL("default behavior, so yy_is_interactive is", b->yy_is_interactive);
}


void input_scan_flush_buffer(input_scan_lexer_t *yy_lexer, INPUT_SCAN_BUFFER_STATE b)
{
  if (!b) {
    return;
  }

  b->yy_n_chars = 0;

  /* We always need two end-of-buffer characters.  The first causes
   * a transition to the end-of-buffer state.  The second causes
   * a jam in that state.
   */
  b->yy_ch_buf[0] = YY_END_OF_BUFFER_CHAR;
  b->yy_ch_buf[1] = YY_END_OF_BUFFER_CHAR;

  b->yy_buf_pos = &b->yy_ch_buf[0];

  b->yy_at_bol = 1;
  b->yy_buffer_status = YY_BUFFER_NEW;

  if (b == yy_lexer->yy_current_buffer) {
    input_scan_load_buffer_state(yy_lexer);
  }
}



